drop database quiztionnaire;
create database if not exists quiztionnaire;
use quiztionnaire;

create table if not exists users(
id int auto_increment primary key,
name varchar(255) not null, 
password varchar(255) not null,
email varchar(255) not null
)ENGINE=InnoDB;

create table if not exists tests( 
id int auto_increment primary key, 
subject varchar(255) not null, 
test_date varchar(255) not null, 
grade smallint not null,
student_id int not null,
foreign key (student_id) references users(id))ENGINE=InnoDB;

create table if not exists questions(
id int auto_increment primary key,
subject varchar(255) not null,
rightanswer varchar(255) not null, 
wronganswer1 varchar(255) not null, 
wronganswer2 varchar(255) not null, 
wronganswer3 varchar(255) not null,
text TEXT not null)ENGINE=InnoDB;

